void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv 	= fragCoord.xy / iResolution.xy;
	vec3 bg 	= texture(iChannel0, uv).rgb;
	vec2 rand   = uv + 0.2*cos(37.0*iTime+uv*1.3)+ 0.1*cos(14.0*iTime+uv*1.1);
	vec3 noise 	= texture(iChannel1, rand).rgb;
	vec3 noise2 = texture(iChannel2, rand.yx).rgb;

	float a0 = cos(uv.y*2.0 + iChannelTime[0] * 1.0) * 0.1;
	float a1 = cos(uv.y*4.0 + iChannelTime[0] * 2.0) * 0.05;
	float a2 = cos(uv.y*8.0 + iChannelTime[0] * 4.0) * 0.025;
	float a3 = cos(uv.y*16.0 + iChannelTime[0] * 8.0) * 0.0125;

	float x = sin(iChannelTime[0]*0.5  +a0+a1+ a2+ a3);


	float diff = pow((1.0 - abs(x - uv.x)), 32.0);

	vec3 grey = mix(vec3(dot(vec3(0.3,0.5,0.2), bg)), bg, noise*noise) - pow(noise,vec3(2.0));
	grey = pow(grey, vec3(0.7+noise2));
	vec3 color = mix(bg, grey, step(x, uv.x));
	fragColor.rgb = color + grey * diff*5.0;
}
