void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    float st = abs(sin(t * 1.0));
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float r = sqrt(nuv.x * nuv.x + nuv.y * nuv.y);
    float theta = atan(abs(nuv.y / nuv.x));


    vec2 polaruvraw = vec2(cos(theta) * r, sin(theta) * r);
    vec2 polaruvrawrotating = vec2(cos(theta + t) * r, sin(theta + t) * r);

    float mm0 = texture(iChannel1, vec2(abs(polaruvrawrotating.x), 0.25)).x;
    float mm1 = texture(iChannel1, vec2(pow(length(vec2(0.0) - polaruvrawrotating), 4.0), 0.25)).x;

    float nn = texture(iChannel0, uv).x;
    float pnn = texture(iChannel0, polaruvrawrotating).x;
    float tnn = texture(iChannel0, vec2(mod(t, 1.0), 0.0)).x;

    float r0 = abs(sin(r * 50.0 + t * 2.0));
    float r00 = pow(r0, 80.0 * st * length(polaruvraw) * tnn);
    vec2 polaruv0 = vec2(cos(theta) * r00, sin(theta) * r00);

    float r1 = abs(sin(r * 52.0 + t * 2.0));
    float r11 = pow(r1, 60.0 * st * length(polaruvraw) * tnn);
    vec2 polaruv1 = vec2(cos(theta) * r11, sin(theta) * r11);

    float c0x = polaruv0.x;
    float c0y = polaruv0.y;
    float c0z = polaruv0.x + polaruv0.y;

    float c1x = polaruv1.x;
    float c1y = polaruv1.y;
    float c1z = polaruv1.x + polaruv1.y;

    fragColor.x = c0x - 0.5;
    fragColor.y = c0y + 0.1;
    fragColor.z = c0x + c0y;

    fragColor.z += c1x * r0 * pow(mm1 * 2.0, 12.0 * r0);

    fragColor.x += pow(mm0, 12.0);
    fragColor.y += pow(mm1, 8.0) * 2.0;
    fragColor.z += mm1 * 0.3 + pnn * 0.0;
}
