void mainImage( out vec4 o, vec2 u )
{
    o -= o;
    for (int i = 1; i < 9; i++) {
        if (i > 1+iFrame/99%8) break;
        float fi = float(i)*.04;
        o += abs(tan(length(mod(u/300.,fi)-fi*.5)*(40./(1.+fi))-mod(iDate.w*(1.+vec4(fi*.01,fi*.03+sin(iDate.w*1e-7),fi*.002,0.)),2e3)))*.02;
    }
}
