#define pulse(x, y, z, a) smoothstep(x-z, x+z, a) * smoothstep(y+z, y-z, a)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    float dist = length(uv.y - 0.5);
    float t = fract(iTime * 2.);
    float power = 30. * (1. - pulse(.15, .55, 0.1, t)) + 20.;
    float col = pow(1. - dist, power);
    //fragColor = vec4(col);
    fragColor = col * vec4(0.7) * (pulse(0.1, 0.3, 0.1, t) + 1.);
    if(uv.y > 0.5) {
        fragColor = vec4(0.);
    }
}
