void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 o = vec3(0.);
    for (float i = 1.; i < 4.; i += 0.2) {
        vec2 p = (fragCoord/iResolution.xy-.5)*i*mat2(vec2(sin(i+iDate.w),cos(i+iDate.w)).xyyx);
        o += texture(iChannel0, p).xyz;
    }
    fragColor = vec4(o/10.,1.);
}
