void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 ret = vec3(0.1);

    float size = 0.04;
    size /= pow(1.5, iTime);

    float the = 3.141592 * 0.25 * iTime;
    r.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));

    for(float i = 0.0+ floor(iTime / 1.7); i < 10.0+ floor(iTime / 1.7); i ++){
        bool b = false;
        if(r.x >= -size * pow(2.0, i) && r.x <= size * pow(2., i) && r.y >= -size * pow(2., i) && r.y <= size * pow(2., i)){
            b = true;
            if(r.x >= -size * pow(2., i) + 0.02 && r.x <= size  * pow(2., i) - 0.02 && r.y >= -size * pow(2., i) + 0.02 && r.y <= size  * pow(2., i) - 0.02){
                b = false;
            }
        }
        if(b){
        	ret = vec3(1.0);
        }
    }

    r *= 1.4142; 
    the = 3.141592 * 0.25;
    r.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));

    for(float i = 0.0 + floor(iTime / 1.7); i < 10.0 + floor(iTime / 1.7); i ++){
        bool b = false;
        if(r.x >= -size * pow(2.0, i) && r.x <= size * pow(2., i) && r.y >= -size * pow(2., i) && r.y <= size * pow(2., i)){
            b = true;
            if(r.x >= -size * pow(2., i) + 0.02 && r.x <= size  * pow(2., i) - 0.02 && r.y >= -size * pow(2., i) + 0.02 && r.y <= size  * pow(2., i) - 0.02){
                b = false;
            }
        }
        if(b){
        	ret = vec3(1.0);
        }
    }

	fragColor = vec4(ret,1.0);
}
