vec2 rotate(vec2 v,float a){
	mat2 R = mat2(cos(a), sin(a), -sin(a), cos(a));
	return R*v;
}

#define ITERATIONS 8
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	//v.x ranges from [-0.5,0.5], and v.y varies at the same rate as v.x
	vec2 v = -.3*(iResolution.xy-2.0*fragCoord.xy)/iResolution.x;

	for(int i = 0; i < ITERATIONS; ++i) {
		v = rotate(abs(v), iTime*(4.0*0.5/float(i+1))) + 0.01;
	}
	float c = smoothstep(0.10,0.101,length(v));
	fragColor = vec4(c,c,c,1.0);
}
