float PI = 3.1415926;

float easeWithSteps(float t, float steps)
{
	float frac = 1.0 / steps;	
	float eT = mod(t, frac);
	float x = eT / frac;
	return t - eT + frac * x*x*x*(x*(x*6.0 - 15.0) + 10.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 mouse = iMouse.xy / iResolution.xy- vec2(.5,.5);
	vec2 coord = uv - vec2(.5,.5);
	coord.y *= iResolution.y / iResolution.x;

	float angle = atan(coord.y, coord.x);
	float dist = length(coord);

	float t = mod(iTime / 10.0, 1.0);
	float eT = easeWithSteps(t, 4.0);
	float sT = sin(eT * PI * 2.0);
	float baseFactor = 48.0 + 2.0 * sin(.1 * iTime * PI);
	float factor = baseFactor;
	if (dist < .20) factor = baseFactor + sT * 24.0;
	float brightness = .5 + .25 * sin(1.0 * PI * 2.0 *mouse.x + coord.x * factor) +
		.25 * sin(1.0 * PI * 2.0 *mouse.y+coord.y * factor);

	//float brightness = .25 + .25 * 
		//sin(48.0*angle + dist*PI + sin(angle*1.0)*(dist + (.5+.5*sin(-PI/2.0+iTime*PI))*mod(iTime,2.0)) * 2.0 * PI);
	///brightness += .25 + .25 * sin(pow(dist,.5) / .707 * PI * 32.0 - iTime * PI * .5);
//	if (dist < .01) brightness *= (dist / .01);
	vec4 color = vec4( brightness, brightness, brightness, 1.0);
	fragColor = color;
}
