void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


	vec2 reso = iResolution.xy;

	reso.x -= 250.0;
	reso.y -= 55.0;

	vec2 uv = fragCoord.xy / reso;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

	uv.x += 0.2;
	uv.y += 0.8;

	vec2 position = uv - 1.0;
	vec2 z = position;

		for(int i = 0; i < 20; i++)
		{ 	
			position.y *= sin(iTime*-0.3);  					
			position.x *= cos(iTime*-0.6);

			z = position + vec2(
            		z.x*z.x-z.y*z.y,
            		2.0*z.x*z.y);
            	if(length(z) > 1.5){
            		fragColor = vec4(0.0, 0.0, 0.0, 1.0);

            	}
            }
}
