#define PI 3.14159265359	

float noise (vec2 p) {
	return texture (iChannel0, p).r;	
}

float fbm (vec2 p) {
	float f = 0.0;
	float q = 0.0;
	float a = 0.0;
	for (float i = 1.0; i <= 5.0; ++i) {
		q = pow (2.0, i);
		a = a + 1.0/q;
		f = noise (p * (q + i/10.0))/q + f;			
	}
	return f/a;
}

vec3 noise3v (vec2 p) {
	return texture (iChannel0, p).rgb;	
}

vec3 fbm3v (vec2 p) {
	vec3 f = vec3 (0.0);
	float q = 0.0;
	float a = 0.0;
	for (float i = 1.0; i <= 5.0; ++i) {
		q = pow (2.0, i);
		a = a + 1.0/q;
		f = noise (p * (q + i/10.0))/q + f;			
	}
	return f/a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (2.0 * fragCoord.xy - iResolution.xy)/min (iResolution.x, iResolution.y);	
	vec2 uv0 = uv;


	vec3 nd = fbm3v (uv/256.0 + vec2 (1.0, 0.0) * iTime/1024.0)-0.5;
	uv = uv+0.5*nd.xy;
	float nv = fbm (uv/256.0 + vec2 (0.0, -1.0) * iTime/1024.0);

	float imp = abs (sin ((nv + iTime/10.0)*PI*5.0));

	vec3 uv1 = normalize (vec3 (uv0, 1.0) + 0.1*nd);

	fragColor = vec4 (pow (imp, 1.4), pow (imp, 5.2), pow (imp, 9.3), 1.0) + 
		texture (iChannel2, vec3 (uv1));
}
