vec3 nrand3( vec2 co )
{
	vec3 a = fract( cos( co.x*8.3e-3 + co.y )*vec3(1.3e5, 4.7e5, 2.9e5) );
	vec3 b = fract( sin( co.x*0.3e-3 + co.y )*vec3(8.1e5, 1.0e5, 0.1e5) );
	vec3 c = mix(a, b, 0.5);
	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 ruv = fragCoord.xy / iResolution.xy;


    //Simplicity Galaxy's starfield + scrolling code
    vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.;
	vec2 uvs = uv * iResolution.xy / max(iResolution.x, iResolution.y);
	vec3 p = vec3(uvs / 4., 0) + vec3(1., -1.3, 0.);
	p += .15 * vec3(sin(iTime / 16.), sin(iTime / 12.),  sin(iTime / 128.));

    vec3 col = vec3(0.);

    vec2 seed = (p.xy * 2.0);	
	seed = floor(seed * iResolution.x);
	vec3 rnd = nrand3( seed );
    col += vec3(pow(rnd.y,30.0));

    float rps0 = .5+sin(iTime)*.3;
    float rps1 = .5+cos(iTime)*.3;
    float rps2 = .5+sin(3.14159+iTime)*.3;
    rps0 = abs(ruv.y-rps0);
    rps1 = abs(ruv.y-rps1);
    rps2 = abs(ruv.y-rps2);
    if(rps0 < .08) col = vec3(1., .5, 0.)*pow(1.-(rps0/.08), .5);
    if(rps1 < .08) col = vec3(0., .5, 1.)*pow(1.-(rps1/.08), .5);
    if(rps2 < .08) col = vec3(0.5, 0., 1.)*pow(1.-(rps2/.08), .5);
	fragColor = vec4(col,1.0);
}
