void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 color;
    vec2 pos = fragCoord.xy;

    float r = 0.0;
    float g = 0.0;
    float b = 0.0;


    for(float i=1.0; i<4.0; i++){

        vec2 center = vec2(sin(iTime*2.0+i*20.0)*iResolution.x*0.1+iResolution.x*0.5, 
                        cos(iTime*2.0+i*10.0)*iResolution.x*0.1+iResolution.x*0.2);

        float dist = distance (pos, center);     

        if(i==1.0){
        	r+= sin(dist/180.0+iTime*2.0)*0.5+0.5;

        }
        if(i==2.0){
        	g+= sin(dist/180.0+iTime*2.0)*0.5+0.5;


        }
        if(i==3.0){
        	b+= sin(dist/180.0+iTime*2.0)*0.5+0.5;

        }

    }

    color= vec4 (r, g, b, 1.0);

	fragColor = color;
}
