void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	uv.x = (uv.x - 0.5) * 2.0;

    float PI = 3.14159265;
    float inc = 0.15;
    float p = (0.5 + 0.5 * sin(iTime)) * 5.5;

    vec3 col = vec3(0.0, 0.0, 0.0);


    float y1 = 1.0 - pow(abs(uv.x), p);

    float y2 = pow(cos(PI * uv.x / 2.0), p);

    float y3 = 1.0 - pow(abs(sin(PI * uv.x / 2.0)), p);

    float y4 = pow(min(cos(PI * uv.x / 2.0), 1.0 - abs(uv.x)), p);

    float y5 = 1.0 - pow(max(0.0, abs(uv.x) * 2.0 - 1.0), p);

    if (y1 > uv.y) {
    	col += vec3(inc);    
    }
    if (y2 > uv.y) {
		col += vec3(inc);
    }
	if (y3 > uv.y) {
		col += vec3(inc);
    }
    if (y4 > uv.y) {
		col += vec3(inc);
    }
    if (y5 > uv.y) {
		col += vec3(inc);
    }

    fragColor = vec4(col, 1.0);
}
