void mainImage(out vec4 color, in vec2 pixCoords)
{
    vec2 posCenter = vec2(0.5*iResolution.x, 0.5*iResolution.y);

    vec2 newCoords = pixCoords - posCenter ; 

    float rayon =  0.2*iResolution.y;

    vec3 soleil = vec3(cos(iTime),0.0,sin(iTime)) ;


    vec4 colorBase = vec4(1.0, 0.4, 1.0, 1.0);

    vec3 vecteurCentrePoint = vec3(newCoords.x, newCoords.y, sqrt(pow(rayon, 2.0) - pow(newCoords.x, 2.0) - pow(newCoords.y, 2.0)));

    vec3 haut = vecteurCentrePoint / rayon;
    float lum = haut.x * soleil.x + haut.y * soleil.y + haut.z * soleil.z ;


	if (sqrt(pow(newCoords.x, 2.0) + pow(newCoords.y, 2.0)) < rayon)
    {
       color = colorBase* max(lum,0.2); 
    }
    else
    {
        color = vec4(0.3, 1.0, 1.0, 1.0);
    }  
}
