void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy; 
    vec4 texColor = texture(iChannel0,xy);

    fragColor = vec4(pow(texColor.r,0.5),pow(texColor.g,0.5),pow(texColor.b,0.5),1.0);
	vec4 red = vec4(1.0,0,0,1.0);
    float dist = sqrt(pow(fragColor.r-red.r,2.0)+pow(fragColor.g-red.g,2.0)+pow(fragColor.b-red.b,2.0));
    if (dist <= 0.6)
        fragColor = texColor;
    else {
                	float average = (texColor.r+texColor.g+texColor.b)/5.0;  
    vec4 grayScale = vec4(average,average,average,1.0); 
    fragColor =  grayScale; 
    }


}
