void mainImage( out vec4 fragColor, in vec2 fragCoord )
{



	vec2 uv = fragCoord.xy / iResolution.xy;
    float mscroll=uv.x+cos(iTime*1.0)*0.25;
    float fscroll=cos(iTime*1.0)*0.25;
    float absolutx=abs((fscroll+uv.x-0.5)/(-uv.y+0.5));
         uv.x *=iResolution.x/iResolution.y;


    absolutx=fract(1.45-absolutx*1.5);



	absolutx=round(absolutx-0.38);
    //absolutx=pow(1.5,((-uv.y*0.55)+absolutx*1.8)*2.0);
    float sky;
    float hlines;
    float vlines;
    if(uv.y<0.5){
    //hlines=fract(pow((uv.y*1.0)+0.75,10.0)+iTime);
    hlines=fract((1.0/(0.6-uv.y)+iTime));

    }else{
    hlines=0.0;
    absolutx=0.0;

    float skyx=(+cos(mscroll*4.0+4.0)*1.3+(uv.y+0.35)*6.28)*0.25+
        (+cos(mscroll*12.0+0.8)*1.3+(uv.y+0.35)*6.28)*0.25+
        (+cos(mscroll*18.0+1.2)*1.3+(uv.y+0.35)*6.28)*0.25+
        (+cos(mscroll*13.0+1.4)*1.3+(uv.y+0.35)*6.28)*0.25;


	sky=round((cos(skyx)-0.4995));
    }
    hlines=round(hlines-0.35);
    float end= (hlines+absolutx)+sky;

    //end=absolutx;
	fragColor = vec4(end,end,end,1.0);
}
