void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = texture(iChannel0, fragCoord/iResolution.xy)*100.;
}

float hash(vec2 p) {
    return fract(dot(p+vec2(.36834,.723), normalize(fract(p.yx*73.91374)+1e-4))*7.38734);
}

vec3 nonUniformRandomDirection(vec2 s) {
    vec3 r = vec3(hash(s),hash(s*.8723),hash(s*.9574))*2.-1.;
    return normalize(r);
}

vec3 uniformRandomDirection(vec2 s) {
    vec3 r = vec3(hash(s),hash(s*.8723),hash(s*.9574))*2.-1.;
    return normalize(r/cos(r));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame < 120) {
        fragColor = vec4(0.);
        return;
    }

    vec3 dir = uniformRandomDirection(fragCoord+mod(iTime*.01,4096.)*27.4723);

    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.x;
    fragColor = mix(texture(iChannel0, fragCoord/iResolution.xy),
                    vec4(max(1.-length(uv*2.-dir.xy/(2.+dir.z))*10.,0.)),
                    .01);
}
