#define PI 3.1415926535897932384626433832795
#define half_PI 1.570796326794896619231321692

float max_dist = distance(vec2(0.5, 0.5), vec2(1.0, 0.5));


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float dist = distance(uv, vec2(0.5, 0.5)) / max_dist;

    float a = mod((atan(uv.y - 0.5, uv.x - 0.5) + half_PI) / PI, 1.0);
    if (uv.x < 0.5) a = 1.0 - a;

    float c0 = texture(iChannel0, vec2(a, 0.25)).x;

    fragColor = vec4(1.0-c0*c0, .5/c0-a, a-c0, 1.0);

//    if (dist >= c0) {
  //      fragColor.rgb *= (c0 / dist) * 1.3 * a;
    //} 

}
