void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 0.1;
	vec2 uv = zoom * (fragCoord.xy - iResolution.xy / 2.0);

    float t = pow(iTime, 0.5);

    float x = uv.x;
    float y = uv.y;

    float m = 2. * sin(t / 4.);
    float n = 2. * sin(t / 9.);

    float i = t;
    float j = t;

    float value = (
       i * sin(m*x) * sin(n*y) + j * sin(n*x) * sin(m*y)
    );


    float color = sin(value) * 3.0;

    if(color > 0.0) {
      fragColor = color - vec4(0,1.5,2,0); 
    } else {
      //fragColor = vec4(med, med, low,1.0);
      fragColor = -color - vec4(1,1.5,0,0); 
    }
}
