void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    float tile = 200.0;
    vec2 oo =  sin(uv*tile + vec2(0.0,iTime*10.0))*0.5 + 0.5;
    float doo = smoothstep(0.2, 0.8, 1.0 - dot(oo, vec2(1.0)));

	fragColor = texture(iChannel0, fragCoord / iResolution.xy)*doo;
}
