void mainImage( out vec4 color, in vec2 fragCoord )
{
    float x = fragCoord.x;
    float y = fragCoord.y;

    float fx = (cos((x+6.0)/4.0) * 12.0 + 2.0);//rouge
    float fx2 = (cos(x/2.0)* 13.0 + 1.0);//turquoise

    float fx3 = (sin((x+25.0)/4.0) * 12.0 + 2.0); //bleu
    float fx4 =( sin ((x+3.0)*sin(iTime)*4.0) * 12.0 + 2.0)*14.0;//rose

    float circleX = 450.0;
    float circleY = 220.0;

    float deltaX = x - circleX;
    float deltaY = y- circleY;
    float distance = sqrt(deltaX*deltaX + deltaY * deltaY);



    if (y<=fx){
        color=vec4 (1.0, 0.3, 0.3, 1.0);
    }
    else if (y<=fx2){
        color=vec4 (0.0, 1.0, 0.5, 1.0);
    }
    else if (y<=fx3){
        color=vec4 (0.0, 0.5, 1.0, 1.0);
    }
    else if(y<=fx4){
        color=vec4 (1.0, 0.5, 1.0, 1.0);
    }
    else if (distance < 150.0){
        color = vec4(1.0, 1.0, 1.0, 1.0);
    }

    else {
        color=vec4 (0.0, 0.0, 0.0, 1.0);
    }
}
