float barsize = 0.01;
int count=0;// unused !
vec2 position;
vec3 color=vec3(0);



vec3 mixcol(float value, float r, float g, float b)
{
	return vec3(value * r, value * g, value * b);
}

void bar(float pos, float r, float g, float b)
{
	 if ((position.y <= pos + barsize) && (position.y >= pos - barsize))
		color = mixcol(1.0 - abs(pos - position.y) / barsize, r*4., g*4., b*4.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;

	float gt = iTime/5.;

    float d = smoothstep(0.2,0.61+0.2*sin(gt*30.)+.9*atan(q.x*q.y)*10.,length(q-vec2(0.5,.8)));


  	vec3 tc=texture(iChannel0,q).rgb/0.8-d;
    // stolen !try to catch max color of texture !
    float x_pos_in_hex = floor(tc.z * 40.) / 20.;
	vec3 white = vec3((1. + sin(gt + x_pos_in_hex)) / 2.,
			  (1. + sin(gt+ x_pos_in_hex)) / 2.,
			  (1. +  cos(gt + x_pos_in_hex)) / 2.);




    for(float i=0.0;i<3.8;i+=.5)
    {
  	    bar(-2.+i+abs(sin(1.-gt+tc.x*tc.y ))+1.5,1.-float(white),1.-float(white),1.- float(white));


    }


    fragColor = vec4((tc-(1.-tc.x*1.0))*1.-color,1.0);


}
