#define PI 3.14159265
#define NSPOKES 12.0
#define WIGG 30.0
#define ZOOM_SPEED 8.0
#define SPIN_SPEED 2.0
#define MAX_SPIRAL 2.0

vec3 paint(vec2 p) {
    float spiraliness = MAX_SPIRAL * sin(iTime);
	p -= 0.5;
    float r = length(p);
    float angle = atan(p.y, p.x) + spiraliness * r;
    float k = fract(angle * NSPOKES / PI / 2.0 + sin(WIGG * r - ZOOM_SPEED * iTime) / r / 18.0 + SPIN_SPEED * iTime);
    float pct = smoothstep(0.5, 0.6, k) + smoothstep(0.1, 0.0, k);
    float fade = 1.0 / (1.0 + r * r * 1020.0);
    pct = mix(pct, 0.5, fade);
    float color_angle = iTime - 3.0 * r;
    vec3 color_1 = mix(vec3(0.5 + 0.5 * sin(color_angle + PI/2.0),
                            0.5 + 0.5 * sin(color_angle + PI/2.0),
                            0.5 + 0.5 * sin(color_angle + -PI/2.0)),
                       vec3(0.5 + 0.5 * sin(color_angle + 0.0),
                            0.5 + 0.5 * sin(color_angle + -PI/2.0),
                            0.5 + 0.5 * sin(color_angle + PI/2.0)),
                       pct)
        + 0.55 / (1.0 + 990.0 * r * r);
    return color_1;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(paint(uv), 1.0);
}
