void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;    

    // Static Color
    //vec4 a = vec4(0.9882352941176471, 0.07450980392156863, 0.21568627450980393,1.0);
    //vec4 b = vec4(0.7686274509803922, 0.10980392156862745, 0.6274509803921569,1.0);

    // Dynamic color
    vec3 colA = 0.5 + 0.5*sin(iTime+uv.xyx+vec3(0,1,2));
    vec4 a = vec4(colA, 1.0);
    vec3 colB = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec4 b = vec4(colB, 1.0);

    // webcam
    //vec3 colA = 0.5 + 0.5*sin(iTime+uv.xyx+vec3(0,1,2));
    //vec4 a = vec4(colA, 1.0);
    //vec4 b = texture(iChannel0, uv);

    // MIX
    float step1 = 0.0;
    float step2 = 1.0;
    vec4 color = mix(a, b, smoothstep(step1, step2, uv.y));

    vec4 logo = texture(iChannel1, uv);
    vec4 newColor = mix(color, logo, vec4(step(0.5, logo.w)));

    // Output to screen
    fragColor = newColor;
}
