#define PI 3.14159265359

vec3 red = vec3(255,179,186); //red
vec3 yellow = vec3(255,255,186); //yellow
vec3 green = vec3(186,255,201); //green
vec3 blue = vec3(186,255,255); //blue

float fastdist(in vec3 a, in vec3 b)
{
    return (a[0]-b[0])*(a[0]-b[0])
            	+(a[1]-b[1])*(a[1]-b[1])
                +(a[2]-b[2])*(a[2]-b[2]);
}

vec3 quantize(in vec3 c) 
{
    c = c*255.0;
    vec3 o = red;
    float d = fastdist(c, red);
    float d2 = fastdist(c, yellow);
    if (d2 < d) {
        o = yellow;
        d = d2;
    }
    d2 = fastdist(c, green);
    if (d2 < d) {
        o = green;
        d = d2;
    }
    d2 = fastdist(c, blue);
    if (d2 < d) {
        o = blue;
        d = d2;
    }
    return o;
}

float radial(in vec2 uv)
{    
    float cx = cos(2.0*iTime)/2.0+uv.x;
    float cy = sin(iTime)/2.0-0.5+uv.y;
    return 1.5*sqrt(cx*cx+cy*cy);
}

float trigofield(in vec2 uv)
{    
    vec2 offset = vec2(6.0*cos(iTime), 3.0*sin(2.0*iTime));
    return sin(2.0*PI*uv.x+offset.x)*cos(2.0*PI*uv.y+offset.y);
}

float trigofield2(in vec2 uv)
{
    return 0.6*cos(uv.x*exp(sin(uv.y))+iTime);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/min(iResolution.x, iResolution.y)*0.6;

    float c = radial(uv);
    c += trigofield(uv);
    c += trigofield2(uv);

	float r = c;
    float g = sin(exp(c)/2.0);
    float b = cos(c*2.0);

    fragColor = vec4(quantize(vec3(r,g,b))/255.0,1.0);
}
