// forked from s23b : https://www.shadertoy.com/view/4st3WX

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy / .5 - 1.;
    uv.x = abs(uv.x);
    uv.x -= .45;
    uv.x *= iResolution.x / iResolution.y;

    // make a tube
    float f = 1. / length(uv);

    // add the angle
    f += atan(uv.x, uv.y) / acos(0.);

    // let's roll
    f -= iTime;

    // make it black and white
    f = floor(fract(f) * 2.);

    // add the darkness to the end of the tunnel
    f *= sin(length(uv)*25. - .1);

    uv.x *=.4;
    f = min(f,1. - smoothstep(.27,.28,length(uv * vec2(1.,sin(iTime)*.9 + 1.5))));

    fragColor = vec4(f, f, f, 1.0);
}
