float lerp(float start, float end, float t) {
	return start + t * (end - start);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    const float minAmpl = 0.005;
    const float maxAmpl = 0.20;
    const float minFreq = 0.0;
    const float maxFreq = 30.0;

   	vec2 pxpos = fragCoord.xy / iResolution.xy;
    float t = sin(0.5 * iTime);
    float q = lerp(minAmpl, maxAmpl, t) * sin(lerp(minFreq, maxFreq, t) * pxpos.y + iTime);
    vec4 texColor = texture(iChannel0, vec2(pxpos.x + 0.5 * q,pxpos.y));
    fragColor = texColor;
}
