void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 tc=texture(iChannel0,uv).rgb;

    // divide screen by/3 .. 0->.33  .. 33->.66 ...66->1.0
    if(uv.x>0.0 && uv.x<0.33) tc.rgb*=vec3(0.0,0.0,1.0); // b
    if(uv.x>0.33 && uv.x<0.66) tc.rgb*=vec3(1.0,1.0,1.0);//w
    if(uv.x>0.66) tc.rgb*=vec3(1.0,0.0,0.0);             //r

	fragColor = vec4(tc,1.0);
}
