// Fork of " Tiny Clouds - filamenty 2" by FabriceNeyret2. https://shadertoy.com/view/Xddczs
// 2018-03-01 12:17:35

// variant of https://shadertoy.com/view/XdtyRs


//#define T texture( iChannel0, ( f += f ) * p.xzw/16. ).y
#define T ( 1.- abs( 1. - 2.* texture( iChannel0, ( f += f ) * p.xzw/16. ).y ))

void mainImage(out vec4 O, vec2 U) {
    vec4 p, d = vec4(.8,0,U/iResolution.y-.8);
    O = vec4(.6,1,0,0) + d.w;
    for( float f, t = 8e2 ; --t > 0. ; p = .01*t*d )
        p += .3*iTime,
        O -= .0007,
        f = .5, f = (T*T*T -.75) * 9.,
        f > 0. ? O += ( vec4(.8,.6,.4,0) - O + f-1. ) *f *.4: O; }

