#define PI 3.141592653589793

vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;
    float x = fragCoord.x + sin(iTime/PI) * iResolution.x / 4.0;
    float y = fragCoord.y + cos(iTime/PI) * iResolution.y / 2.0;
    float hue = sin(x / iResolution.x) + cos(y / iResolution.y) + sin((x + y) / 500.0) + cos(sqrt(x * x + y * y) / 100.0);

    col = hsv2rgb(vec3(hue + iTime/8.0, 1.0, mod(iTime, 1000.0)));

    fragColor = vec4(col, 1);
}
