#define RADIUS 0.12
#define FLOW 4.0

bool in_pseudo_quad(vec2 p1, vec2 p2, vec2 uv)
{
	return (uv.x > p1.x - RADIUS &&
            uv.x < p2.x + RADIUS &&
            uv.y > p1.y - RADIUS &&
            uv.y < p2.y + RADIUS);
}

float evaluate(vec2 p1, vec2 p2, vec2 uv)
{
    float BE = abs(uv.y - p1.y);
    float dx = sqrt(RADIUS*RADIUS - BE*BE);
    float X1 = max(p1.x, uv.x - dx);
    float X2 = min(p2.x, uv.x + dx);
    return max(0.0, FLOW * (X2 - X1));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.y / iResolution.x;
    uv.x /= aspect;
 	vec2 p1 = vec2(0.1/aspect, 0.5);
 	vec2 p2 = vec2(0.5/aspect, 0.5);
 	vec2 p3 = vec2(0.9/aspect, 0.5);

    float i1 = 0.0;
    if (in_pseudo_quad(p1, p2, uv)) {
        i1 += evaluate(p1, p2, uv);
    }
    float i2 = 0.0;
    if (in_pseudo_quad(p2, p3, uv)) {
        i2 += evaluate(p2, p3, uv);
    }

    float s = sin(iTime) * 0.5 + 0.5;    
 	fragColor = vec4(i1+i2*(1.0-s), s*i2, 0, 1);
}
