float checker(vec2 p)
{
    p = floor(p);
    return mod(p.x + p.y, 2.0) * 2.0 - 1.0;
}
float totalGrid(vec2 p)
{
    float q = checker(p * 4.0) * 0.1 + 0.7;
    q += checker(p * 8.0) * 0.02;
    q += checker(p * 32.0) * 0.01;
    return q;
}
vec2 lensDistort(vec2 c, float factor)
{
    // [0;1] -> [-1;1]
    c = (c - 0.5) * 2.0;
    // [-1;1] -> film frame size
    c.y *= 3.0/4.0;
    // distort
    c /= 1.0 + dot(c, c) * -factor + 1.6 * factor;
    // film frame size -> [-1;1]
    c.y *= 4.0/3.0;
    // [-1;1] -> [0;1]
    c = c * 0.5 + 0.5;
    return c;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 R = iResolution.xy;
    float factor = 0.1;
    float london = 0.4;
    if (iMouse.z > 0.0)
    {
        factor = iMouse.x / R.x;
        london = iMouse.y / R.y;
    }
    vec2 uv = lensDistort(fragCoord/R, factor)*R/min(R,R.yx);
    uv.y = 1.0 - uv.y;
    vec3 q = vec3(totalGrid(uv));
    q = mix(q, texture(iChannel0, uv / R.x * R.y + vec2(0.0,0.2)).rgb, london);
    // highlight out of bounds areas
    if (uv.x < 0.0 || uv.y < 0.0 || uv.x > R.x/R.y || uv.y > 1.0)
        q = vec3(1.0, 0.0, 0.0);
    // trim the edges
    if (abs(fragCoord.y/R.y*2.0-1.0) > R.x / R.y / 2.35)
        q = mix(q, vec3(0.0), 0.75);
    fragColor = vec4(q,1.0);
}
