//#define USE_MOUSE
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 bgTex = texture(iChannel0, uv);
    vec4 mainTex = texture(iChannel1, uv);

	float grey = dot(bgTex.rgb, vec3(0.299, 0.587, 0.114));
    float bgGrey = dot(mainTex.rgb, vec3(0.299, 0.587, 0.114));

    vec4 color = mainTex;

#ifdef USE_MOUSE
    float _BgDiff = iMouse.x / iResolution.x;
#else
    float _BgDiff = (cos(iTime) + 1.0) / 2.0;
#endif

	if (abs(grey - bgGrey) < _BgDiff) color = vec4(0);

	fragColor = color;
}
