void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 v_TexCoordinateA = fragCoord.xy / iResolution.xy;
    highp vec4 colA = texture(iChannel0, v_TexCoordinateA);
    highp vec4 colB = texture(iChannel1, v_TexCoordinateA);



    colA.a = 1.0;
    colB.a = 1.0;

    float  yy = v_TexCoordinateA.y;
    float  masksize = 0.04;


    float alpha = 0.0;

    if(yy < 0.5 - masksize){
    	alpha = 0.0;
    }else if( yy > 0.5 + masksize){
        alpha = 1.0;
    }else{

        float  index =  yy - (0.5 - masksize);
        float al =  index * 1.0 / (masksize * 2.0);
    	alpha = al;


    }


     fragColor = colA * alpha + colB * (1.0 - alpha);

}
