// Rotation ------------------------------------------------------------

// Rotation around y axis
// v : Vector
// a : Angle
vec3 rotate(in vec3 v,in float a)
{
    return vec3(v.x*cos(a)+v.z*sin(a),v.y,-v.x*sin(a)+v.z*cos(a));
}

// Textures ------------------------------------------------------------
const mat3 m = mat3( 0.00,  0.80,  0.60,
                    -0.80,  0.36, -0.48,
                    -0.60, -0.48,  0.64 );


float hash( float n ) { return fract(sin(n)*43758.5453123); }
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*157.0 + 113.0*p.z;
    return mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                   mix( hash(n+157.0), hash(n+158.0),f.x),f.y),
               mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                   mix( hash(n+270.0), hash(n+271.0),f.x),f.y),f.z);
}

float PerlinNoise3DABS(vec3 p,float alpha,float beta)
{
   float val,sum = 0.0;
   float scale = 1.0;

   for (int i=0;i<5;i++) {
      val = noise(p);
            val = abs(val);
      sum += val / scale;
      scale *= alpha;
      p *= beta;
   }
   return(sum);
}

bool inPlanete(in vec3 p,in vec3 c, in float r){


    float dist = distance(p,c);
     if(dist<0.9)
         return true;
    float f;
    vec3 q = 0.5*p;
    f  = 0.8*noise( q );
    f  += 0.4*noise( 4.0*q );
    f  += 0.2*noise( 8.0*q );
    f  += 0.1*noise( 16.0*q );
    f/=(0.8+0.4+0.2+0.1);
    f  = f/dist/dist/dist/dist/dist/dist;

    return f>0.7;
}

// Objects --------------------------------------------------------------

// Intersection between a ray and a sphere
// o : Ray origin
// d : Ray direction
// c : Center of sphere
// r : Radius
// t : Intersection depth
// n : Normal at intersection point
bool sphere(in vec3 o,in vec3 d,in vec3 c,in float r,out float t,out vec3 n)
{
    vec3 oc = o-c;

    float b=dot(d,oc);
    float k = dot(oc,oc)-r*r;
    t=b*b-k;
    float e = 0.001;
    for(float i=0.0;i<10000.0;++i){
        vec3 p =o+(e*i)*d;
        if(inPlanete(p,c,r)){
            t=e*i;
            return true;
        }
    }

    /*if (t<=0.0) return false;

    t=-b-sqrt(t);
    if (t<0.0) return false;

    // Normal
    n=(o+t*d-c)/r;*/

   return false;
}

// Lighting -------------------------------------------------------------

// Background color
// r : Ray direction
vec3 background(in vec3 r)
{
    return mix(vec3(0.2, 0.3, 0.4), vec3(0.7, 0.8, 1.0), r.y*0.5+0.5);
}

vec3 planet(in vec3 p){
    float h =0.9;
    if(distance(p,vec3(0.0,0.0,0.0))<h)
        return vec3(0.0, 0.0, 1.0);

    else 
   		return mix(vec3(0.0, 1.0, 0.0),vec3(1.0, 1.0, 1.0),(distance(p,vec3(0.0,0.0,0.0))-h)/0.02);

}

// p : Point on object
// n : normal at point
vec3 shade(in vec3 p,in vec3 n)
{
    // Point light
    const vec3 lightPos = vec3(1.0, 1.0,-5.0);
    const vec3 lightColor = vec3(0.95, 0.95,0.95);

    vec3 l = normalize(lightPos - p);

    // Not even Phong shading, use weighted cosine instead for smooth transitions
    float diff = 0.5*(1.0+dot(n, l)*dot(n, l));

    // Change call to Texture there : apply either color() or sine() or checker() or whatever texture you wish
    vec3 c = planet(p)*lightColor;//1.0*marbre(p,n)*

    return c;
}

// Vignetting    
// c : Color
// p : Point in screen space
vec4 Vignetting(in vec4 c,in vec2 p)
{
                return c * ( 0.5 + 0.5*pow( (p.x+1.0)*(p.y+1.0)*(p.x-1.0)*(p.y-1.0), 0.1 ) );
}


// Main -----------------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = -1.0 + 2.0*fragCoord.xy/iResolution.xy;
    vec2 uv = xy * vec2(iResolution.x/iResolution.y, 1.0);

                // Ray origin and direction
    vec3 o = vec3(0.0, 0.0, -2.0);
                vec3 d = normalize(vec3(uv, 1.0));

    o=rotate(o,0.5*iTime);
    d=rotate(d,0.5*iTime);

                vec3 n;
    float t;

    // Default background color
    fragColor=vec4(background(d),1.0);
    if (sphere(o, d, vec3(0.0, 0.0, 0.0), 1.0, t, n))
    {         
        fragColor = vec4( mix(background(d), shade(o+d*t,n), step(0.0, t)), 1.0 );
    }
    fragColor=Vignetting(fragColor,xy);
}
