void mainImage( out vec4 O, in vec2 f ){
    vec2 r = iResolution.xy, uv = f/r,  u = f/r-0.5,
    m = iMouse.xy/r,  p = fract(f/r.x*20.0);
    float t = m.y*5. + (uv.x < .5 ? .3 : 0.);
    t *= uv.x < .5 ? -1. : 1.;
    float rand = .02,  d = abs(uv.x-.5),  e = mod(uv.y*0.7+t,.7),
    images = floor(e+0.7) * (step(d,.5 - rand) - step(d,rand*.1)),
    grid = step(p.x*p.y,.97), logo = step( abs(u.y) + abs(u.x),0.1);
    O = vec4(grid - logo - images *0.8);
}
