void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float c_TileSize = 32.0;    // tile size
    const float c_LightRadius = -1.0/5.0;  // light radius (in tiles)

    const float c_LightPower = 0.999;  

    vec2 discrete;
    discrete.x = length(floor((gl_FragCoord.x + c_TileSize/2.0)/c_TileSize)*c_TileSize - iMouse.x) / c_TileSize;
    discrete.y = length(floor((gl_FragCoord.y + c_TileSize/2.0)/c_TileSize)*c_TileSize - iMouse.y) / c_TileSize;

    float distance = exp(length(discrete) * c_LightRadius);
    float light_intensity = (distance) * c_LightPower;

    vec2 uv = gl_FragCoord.xy / c_TileSize / 1.5;
    fragColor = texture(iChannel1, uv); 

    float localTime = (iTime * 0.01f) * (1.1f - distance);
    vec3 lightColor = texture(iChannel0, uv + vec2(localTime)).rgb; 
    lightColor = vec3((lightColor.r + lightColor.g + lightColor.b) / 3.0);
    lightColor += vec3(light_intensity, light_intensity * 0.5, light_intensity*0.2);
    lightColor *= light_intensity;

    fragColor.rgb = mix(lightColor, fragColor.rgb, distance);
    fragColor.rgb = mix(lightColor, fragColor.rgb, 1.0f - distance);
}
