float funcP(in vec2 coord)
{
    float x = coord.x;
    float y = coord.y;
    float w = iResolution.x; //w = 320.0;
    float h = iResolution.y; //h = 100.0;
    return sin(sqrt(((x - w / 2.0)* (x - w / 2.0) + (y - h / 2.0) * (y - h / 2.0))) / 8.0) * tan(iTime);
}

float funcC(in vec2 coord)
{
    float xw = (coord.x + iResolution.x)/2.0;
    float yh = (coord.y + iResolution.y)/2.0;
    return sin( sqrt(xw * xw + yh * yh) + iTime );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord.x = fragCoord.x*sin(iTime);
    fragCoord.y = fragCoord.y*cos(iTime);
    float c = 1.5;
    float r, g, b;
    if (sin(iTime) > 0.5)
    {
        r = sin(funcP(fragCoord)*c);
    	g = cos(funcP(fragCoord)*c);
    	b = tan(funcP(fragCoord)*c);
    }
    else
    {
        r = sin(funcC(fragCoord)*c);
    	g = cos(funcC(fragCoord)*c);
    	b = tan(funcC(fragCoord)*c);
    }

    fragColor = vec4(r,g,b,1.0);
}

float funcC(float x, float y, float w, float h)
{
    float xw = (x + w)/2.0;
    float yh = (y + h)/2.0;
    return sin( sqrt(xw * xw + yh * yh) );
}

vec2 mainSound( in int samp, float time )
{
    float w = 600.0;
    float h = 420.0;
    float x = float( mod(time,w) );
    float y = time / h;
    float value = funcC(x, y, w, h);
    return vec2( sin(6.2831*440.0*value)*100.0 );
}
