void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float totalZoom = 3.0;
    float zoomAmount = ((cos(iTime) + 1.0) * totalZoom) + 1.0;

    //busWheel
    //vec2 pos = vec2(110, 180)/iChannelResolution[0].xy;

    //chimmey
    //vec2 pos = vec2(200, 382)/iChannelResolution[0].xy;

    //mouse pos
    vec2 pos = iMouse.xy / iResolution.xy;

	vec2 UV = fragCoord.xy / iResolution.xy;
    vec2 textureXY = UV/zoomAmount + pos;
    textureXY -= pos/zoomAmount;
    vec4 sampleRGBA = texture(iChannel0, textureXY);
	fragColor = sampleRGBA;
}
