// stolen from iq website
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float t = texture(iChannel0, uv).x;

    fragColor = palette(
        t + 0.01 * iTime, 
		vec3(0.5, 0.5, 0.5),
        vec3(0.5, 0.5, 0.5),
        vec3(1.0, 1.0, 1.0),
		vec3(0.00, 0.1, 0.2) + vec3(0.05 * iTime)
	).xyzz;
    //fragColor = vec4(t);
}

// ty to FabriceNeyret2
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         h = smoothstep(2., 0., R - abs(U+=U-R));
    float T = iTime;


    U = U/R * rot(0.01 * T)
            * ( 0.985 + 0.15 * (1.0 - pow(sin(.1*T), 2.0)) ) 
            + 0.3 * vec2(cos(0.1 * T), sin(0.1 * T))
        	+ vec2(0.15 * T, 0.0);

	O = mix(texture(iChannel0, fract(.5+.5*U) ), 
            vec4(50)*max(h.x, h.y), 
            .03 + .01 * cos(.1*T)
           );
}
