float line(vec2 p, vec2 a, vec2 b)
{
     vec2 a2b = b - a;
     vec2 a2p = p - a;     

     // å› ä¸ºæŠ•å½± proj(a2p,a2b) ç­‰äºŽ dot(a2p,a2b) / length(a2b)
     // åœ¨æŠŠæŠ•å½±çš„å€¼å’Œa2bå‘é‡åšæ¯”ä¾‹å…³ç³»ã€‚
     // æ‰€ä»¥proj(a2p,a2b)/length(a2b) ç­‰äºŽ dot(a2p,a2b)/dot(a2b,a2b)

     float h = clamp( dot(a2p,a2b)/dot(a2b,a2b), 0.0, 1.0 );
     vec2 p1 = mix( a, b, h );
     return length( p1 - p );
}

float rand(float n)
{
    return fract(sin(n) * 43758.5453123);
}

float noise(float p){
    float fl = floor(p);
    float fc = fract(p);
    return mix(rand(fl), rand(fl + 1.0), fc);
}


void mainImage( out vec4 O, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;


    //uv.y += cos(uv.x*100.0+iTime)*0.05*sin(uv.x*15.0+iTime)*0.5;
    //uv += vec2(cos(iTime), sin(iTime))*0.1;
    uv.y += noise(uv.x*40.0+iTime*2.0)*0.05;
    float t = line(uv, vec2(0.02,0.5),vec2(1.75,0.5));
    O = vec4(0.05, 0.15, 1.0, 1.0)*0.02/t;
}
