#define PI 3.14159265358979

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = (2. * fragCoord - iResolution.xy ) / iResolution.y * 2.;
    vec3 norm = vec3( uv, sqrt( 1.0 - dot(uv,uv) ));
    float s = atan( norm.z, norm.x ) / (2.0 * PI);
    float t = asin( norm.y ) / PI;
    //fragColor = (dot(uv,uv) > 1.0 ? vec4( 0.0, 0.0, 0.0, 1.0 ) : 
    //		texture(iChannel0, vec2( s + iTime * 0.1, t)));
    fragColor = smoothstep(0.1, 0.0, dot(uv, uv) - 1.0) * 
        texture(iChannel0, vec2(s + iTime * 0.05, t));
}
