void mainImage(out vec4 C, vec2 u) {
	C = texture(iChannel0, u/=iResolution.xy);
}

// FabriceNeyret2 version
void mainImage(out vec4 c, vec2 u) {
    vec2 R = iResolution.xy;
	c = length(u-iMouse.xy)/R.y < .01 
            ? abs(sin(iDate.w+vec4(0,1.6,0,0)))
            : texture(iChannel0, u / R);
}

