vec2 warpLocalScalePositionToUse(vec2 center, vec2 p, float r, float R, float intensity, float ratio)
{
    vec2 point = p;
    vec2 p_u = vec2(p.x, p.y * ratio + 0.5 - 0.5 * ratio); 
    vec2 c_u = vec2(center.x, center.y * ratio + 0.5 - 0.5 * ratio);

    float d = distance(p_u, c_u);
    float scale = (R - r) / r * intensity;
    float r_m = r * (1.0 + scale);

    if (d < r_m)
    {
        float alpha = 1.0 / (1.0 + scale);
        point = center + (p - center) * alpha;
    }
    else if (d < R)
    {
        float percent = (R - d) / (R - r_m);
        point = center + (p - center) * (R - percent * (R - r)) / d;
    }

	return point;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 point = uv;
    vec2 center = vec2(0.5);
    float ratio = iResolution.y / iResolution.x;
    float r = 0.2;
    float R = 0.3;
    float intensity = 0.3 * sin(iTime);
    point = warpLocalScalePositionToUse(center, point, r, R, intensity, ratio);

    fragColor = texture(iChannel0, point);
}
