vec3 smoothSine(vec2 uv, vec3 background, float freq, float amp, float size, vec2 shift, vec3 baseCol){
    vec3 ret = background;

    vec2 sine = vec2(uv.x, shift.y + amp*sin(uv.x*freq+shift.x));

    //vector tangent to curve
    //vec2 tang = vec2(uv.x, shift.y + amp*cos(uv.x*freq+shift.x));

    //vector normal to curve
    //vec2 norm = normalize(-1.*vec2(tang.y,tang.x));

    float dist = length(uv-sine);

    if (dist < size){
     ret = background + smoothstep(size,0.,dist);   
    }

 	ret*=baseCol;

    return ret;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 qr = fragCoord/iResolution.xy;
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float freq = smoothstep(.75,.1,(texture(iChannel0, vec2(qr.x,1))).x);

    vec3 baseCol = vec3(cos(iTime)*uv.x*181./255., 199./255., 229./255.);

    vec3 col = vec3(0);

    //should use loop here
    col = smoothSine(uv, col, sin(iTime+.3)*5., freq/7.+.25, .01, vec2(6.*iTime+.3,-0.10625 + sin(2.*iTime)/6.), baseCol);
    col = smoothSine(uv, col, sin(iTime+.25)*5., freq/6.+.25, .0125, vec2(6.*iTime+.25,-0.1125+ sin(2.*iTime+.05)/6.), baseCol);
    col = smoothSine(uv, col, sin(iTime+.2)*5., freq/5.+.25, .015, vec2(6.*iTime+.2,-0.125+ sin(2.*iTime+.1)/6.), baseCol);
    col = smoothSine(uv, col, sin(iTime+.15)*5., freq/4.+.25, .0175, vec2(6.*iTime+.15,-0.15+ sin(2.*iTime+.15)/6.), baseCol);  
	col = smoothSine(uv, col, sin(iTime+.1)*5., freq/5.+.25, .02, vec2(6.*iTime+.1,0.-.2+ sin(2.*iTime+.2)/6.), baseCol);
    col = smoothSine(uv, col, sin(iTime+.05)*5., freq/6.+.25, .0225, vec2(6.*iTime+.05,-.3+ sin(2.*iTime+.25)/6.), baseCol);
    col = smoothSine(uv, col, sin(iTime)*5., freq/7.+.25, .025, vec2(6.*iTime,-.425+ sin(2.*iTime+.3)/6.), baseCol); 

    // Output to screen
    fragColor = vec4(col,1.0);
}
