const float decay = 59.5; //Irgendwas von 59 bis 60

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0,ivec2(fragCoord),0)*(decay*iTimeDelta)+texelFetch(iChannel1, ivec2(fragCoord),0)*(1.-(decay*iTimeDelta));

	fragColor.r = clamp(fragColor.r, -1., 2.);
    fragColor.g = clamp(fragColor.g, -1., 2.);
    fragColor.b = clamp(fragColor.b, -1., 2.);

    if(texelFetch(iChannel2, ivec2(32, 1), 0).r > 0.5)
        fragColor = vec4(0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor =	texture(iChannel0, uv);
}
