#define PI 3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);

    vec2 center = iResolution.xy / 2.0;
    vec2 dist = center - fragCoord;
    float r = length(dist);
    float theta = sin(iTime)+atan(dist.y, dist.x)/(2.0*PI);


    float d = 25.;
    r = r - d*theta;
    float c = r/d - (floor(r/d));

    fragColor =  1.-vec4( smoothstep(.01,.04,abs(c-.025)));

}
