void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 O, in vec2 uv ){
    uv /= iResolution.xy;    
    vec4 old = texture(iChannel1, uv); // buffer           
    float F = float(iFrame);

    float d = .3 + .15 * sin(F * 0.1);
    float t2 = iTime * 2. + sin(F * .002 + 2.);
    vec2 pen = vec2(
        d * cos(t2) * iResolution.y/iResolution.x,
    	d * sin(t2 + cos(F * 0.017))
    ) + .5;

    float dis = distance(uv, pen) / .5;
    if(dis < 1.) {
        float w = sin(F * 0.001) * 0.01;
        vec3 inc = vec3(
            pow(1.07+w-dis, 50.) * .024,
            pow(1.07-dis, 50.) * .022,
            pow(1.07-w-dis, 50.) * .020
        );
      	O.rgb = old.rgb + inc * (sin(F * 0.051) * 0.5);
    } else {
        O = old;
    }

    //clear
    //O.rgb *= 0.0;
}
