#define I_MAX		150
#define E			0.001

vec4	march(vec3 pos, vec3 dir);
vec3	camera(vec2 uv);
void	rotate(inout vec2 v, float angle);
vec2	rot_t;
vec3	h;
vec3	base;
vec3	light_pos;
vec3	light_green = vec3(.5,.7,.4);
vec3	light_blue = vec3(.1,.5,.7);
vec3	light_red = vec3(.7,.2,.1);
vec3	uglob;
float 	t;

void mainImage(out vec4 c_out, in vec2 f)
{
    uglob*=0.;
    vec3	col = vec3(0., 0., 0.);
    vec2 R = iResolution.xy,
          uv  = vec2(f-R/2.) / R.y;
	vec3	dir = camera(uv);
    vec3	pos = vec3(-.0, .0, 1.0-iTime*.5*0.);
    t = iTime;

    dir=normalize(dir);
    dir.xy *= 1.51+sin(iTime);
    rotate(dir.xy, 1.+dir.z*2.*sin(iTime*.51));
    vec4	inter = (march(pos, dir));

    col.xyz = 1.-vec3(.99, .9, .7)*( (inter.w*.006251)+(-.125+inter.x*.006));
    col.xyz += -.5+h*.253061251;
    c_out =  vec4(col,1.0);
}

float	mylength(vec2 p)
{
	float	ret;

    p = p*p*p*p;
    p = p*p;
    ret = (p.x+p.y);
    ret = pow(ret, 1./8.);

    return ret;
}

float	scene(vec3 p)
{
    float	mind = 1e5;
    p.x -= 15.;
    p.z += -t*18.+18.;
    p.xy = (fract(p.xy/30.)-.5)*30.;    
    p.xy += 60.*vec2((cos((p.z*.0625)))*.1, (sin((p.z*.0625)))*.1);

    rotate(p.xy, iTime*1.+p.z*.5);
    p.xy = abs(p.xy)-.5;
    rotate(p.xy, iTime*-1.+p.z*.5);
    p.xy = abs(p.xy)-.25;

    p.xy = abs(p.xy)-.64-sin(p.z*.4+iTime*1.)*.64;

    mind = min(mind, 
              +mylength( (p.xy))-.2410012501
              );
    h += .076125-vec3(.10, .15, .14)*1./(mind*mind +2.05102);
    return(mind)*.25;
}


vec4	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec4	s = vec4(0.0, 0.0, 0.0, 0.0);

    for (int i = -1; i < I_MAX; ++i)
    {
    	p = pos + dir * dist.y;
        p.x -= 20.;
    	rotate(p.zx, 1.+iTime*.5);
        rotate(p.yz, 1.+iTime*.5);
        dist.x = scene(p);
        dist.y += dist.x;
        if (dist.x < E )
        {
            s.y = 1.;
            break;
        }
        s.x++;
    }
    s.w = dist.y+dist.x;
    return (s);
}

// Utilities

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}


vec3 calcNormal( in vec3 pos, float e, vec3 dir)
{
    vec3 eps = vec3(e,0.0,0.0);

	return normalize(vec3(
           march(pos+eps.xyy, dir).w - march(pos-eps.xyy, dir).w,
           march(pos+eps.yxy, dir).w - march(pos-eps.yxy, dir).w,
           march(pos+eps.yyx, dir).w - march(pos-eps.yyx, dir).w ));
}

vec3	camera(vec2 uv)
{
    float		fov = 1.;
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}
