const vec4 colors[5] = vec4[5](
    vec4(0.247059, 0.721569, 0.686275, 1.0),
    vec4(0.498039, 0.780392, 0.686275, 1.0),
    vec4(0.854902, 0.847059, 0.654902, 1.0),
    vec4(1.000000, 0.619608, 0.615686, 1.0),
    vec4(1.000000, 0.239216, 0.498039, 1.0)
);
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 parts = vec2(24.0, 14.0);
    vec2 uv2 = floor(uv * parts) / parts;  
    vec4 c = texture(iChannel0, uv2);
    c = texture(iChannel0, mod(vec2(c.r, sin(c.r))+uv, 1.0));

	fragColor = colors[int(c.r * 5.0)];
}
